import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";

function App() {
  const [features, setFeatures] = useState([]);

  // Gọi API Django để lấy danh sách tính năng
  useEffect(() => {
    fetch("http://localhost:8000/api/features/")
      .then((res) => res.json())
      .then((data) => setFeatures(data))
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  return (
    <div>
      <nav className="navbar navbar-expand-lg navbar-dark bg-black">
        <div className="container">
          <a className="navbar-brand" href="#">HCMUS-STONER</a>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav me-auto">
              <li className="nav-item"><a className="nav-link" href="#">Home</a></li>
              <li className="nav-item"><a className="nav-link" href="#">About</a></li>
              <li className="nav-item"><a className="nav-link" href="#">How to use</a></li>
            </ul>
            <a href="#" className="btn btn-sm btn-light">Đăng Nhập</a>
          </div>
        </div>
      </nav>

      <div className="container content-container">
        <div className="row justify-content-center">
          {features.map((feature, index) => (
            <FeatureCard key={index} title={feature.title} description={feature.description} />
          ))}
        </div>
      </div>
    </div>
  );
}

function FeatureCard({ title, description }) {
  return (
    <div className="col-md-4 d-flex justify-content-center">
      <div className="card text-center text-white p-4 custom-card">
        <h4 className="fw-bold">{title}</h4>
        <p className="text-justify">{description}</p>
      </div>
    </div>
  );
}

export default App;
