import { useState } from "react";
import { useNavigate } from "react-router-dom";

const Register = () => {
    const [tk, setTk] = useState("");
    const [mk, setMk] = useState("");
    const [error, setError] = useState("");
    const navigate = useNavigate();

    const handleRegister = async (e) => {
        e.preventDefault();
        setError("");

        try {
            const response = await fetch("http://127.0.0.1:8000/register", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ tk, mk }),
            });

            const data = await response.json();
            if (response.ok) {
                alert("register sucessfully!");
                navigate("/login");
            } else {
                setError(data.error || "register unsucessfully!");
            }
        } catch (err) {
            setError("Cant connect to server!");
        }
    };

    return (
        <div className="container d-flex justify-content-center mt-5">
            <div className="register-container p-4 bg-dark text-white rounded shadow">
                <h2 className="text-center">Register</h2>
                <form onSubmit={handleRegister}>
                    <div className="mb-3">
                        <label className="form-label">Username:</label>
                        <input 
                            type="text" 
                            className="form-control" 
                            value={tk} 
                            onChange={(e) => setTk(e.target.value)} 
                            required
                        />
                    </div>
                    <div className="mb-3">
                        <label className="form-label">Password:</label>
                        <input 
                            type="password" 
                            className="form-control" 
                            value={mk} 
                            onChange={(e) => setMk(e.target.value)} 
                            required
                        />
                    </div>
                    <button type="submit" className="btn btn-light w-100">Register</button>
                </form>
                <p className="text-center mt-3">
                    Đã có tài khoản? <a href="/login" className="btn btn-sm btn-light">Login</a>
                </p>
                {error && <p className="text-danger text-center">{error}</p>}
            </div>
        </div>
    );
};

export default Register;
