import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:8000/login/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ tk, mk }),
      });
      const data = await response.json();
      if (response.ok) {
        localStorage.setItem("token", data.token);
        navigate("/");
      } else {
        setError(data.error || "Login unsuccessfully");
      }
    } catch (err) {
      setError("Cant connect to server");
    }
  };

  return (
    <div className="container">
      <div className="login-container" style={{ maxWidth: "400px", margin: "50px auto", padding: "30px", backgroundColor: "#616161", borderRadius: "15px", boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)" }}>
        <h2 className="text-white text-center">Đăng Nhập</h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label className="form-label text-white">Username:</label>
            <input type="text" className="form-control" required value={tk} onChange={(e) => setUsername(e.target.value)} />
          </div>
          <div className="mb-3">
            <label className="form-label text-white">Password:</label>
            <input type="password" className="form-control" required value={mk} onChange={(e) => setPassword(e.target.value)} />
          </div>
          <button type="submit" className="btn btn-light w-100">Login</button>
        </form>
        <p className="text-center text-white mt-3">
          Chưa có tài khoản? <a href="/register" className="btn btn-sm btn-light">Đăng Ký</a>
        </p>
        {error && <p className="text-danger text-center">{error}</p>}
      </div>
    </div>
  );
};

export default Login;
